<?php/* * @func: void arrayStripslashes(mixed $var); * @desc: Un-quotes a quoted array or string. * @param: var: Input string or array. * @ret: No value is returned. */	function array_stripslashes(&$var) {		if (is_string($var)) {			$var = stripslashes($var);		} else {			if (is_array($var)) {				foreach ($var AS $key => $value) {					array_stripslashes($var[$key]);				}			}		}	}?>